<template>
  <form @submit="handleSubmit" class="form-container">
    <input type="text" v-model="todo" class="input-text" placeholder="Add Todo..." />
    <button class="input-submit">Submit</button>
  </form>
</template>

<script>
import { defineComponent, ref } from '@vue/composition-api'

export default defineComponent({
  setup(props, context) {
    const todo = ref("");

    const handleSubmit = (event) => {
      event.preventDefault();
      context.emit('addTodo', todo.value)
      todo.value = "";
    } 

    return {
      handleSubmit,
      todo,
    }
  },
})
</script>
