<template>
  <li class="item">
    <div @dblclick="activateEditMode"  class="todo-container">
      <div v-if="!data.isEditing">
        <input
          type="checkbox"
          class="checkbox"
          :checked="completed"
          @change="onChangeHandler"
        />
        <button @click="onDeleteHandler">Delete</button>
        <span :class="{ completed: isCompleted }">
          {{title}}
        </span>
      </div>

      <input
        type="text"
        v-if="data.isEditing"
        class="textInput"
        v-model="title"
        @keydown="handleUpdatedDone"
      />
    </div>
  </li>
</template>

<script>
import { defineComponent, computed, reactive, watch } from '@vue/composition-api'

export default defineComponent({
  props: {
    todo: {
      type: Object,
    }
  },

  setup(props, context) {

    const data = reactive({
      isEditing: false,
    })

    const { id, title, completed } = props.todo;

    const activateEditMode = () => {
      data.isEditing = true;
    }

    const handleUpdatedDone = (event) => {
      const newTodo = {
        ...props.todo,
        title: event.target.value
      }
      context.emit('onUpdateTodo', newTodo);
      if (event.key === "Enter") {
        data.isEditing = false;
      }
    };

    const onChangeHandler = () => {
      context.emit('onUpdateStatus', props.todo.id)
    }

    const onDeleteHandler = () => {
      context.emit('onDeleteTodo', props.todo)
    }

    const isCompleted = computed(() => {
      return props.todo.completed;
    });

    const isEditing = computed(() => {
      return data.isEditing;
    })


         return {
           id,
           title,
           completed,
           data,

           activateEditMode,
           onChangeHandler,
           onDeleteHandler,
           isCompleted,
           isEditing,
           handleUpdatedDone,
         }
  },
})
</script>

<style scoped>
.item {
  font-size: 1.2rem;
  list-style-type: none;
  padding: 17px 0px;
  border-bottom: 1px solid #eaeaea;
}

.checkbox {
  margin-right: 15px;
}

.item button {
  font-size: 13px;
  background: #f1f3f4;
  border: none;
  cursor: pointer;
  float: right;
  outline: none;
  border-radius: 100px;
  height: 50px;
  width: 50px;
  margin: -10px 0 0 10px;
}

.textInput {
  width: 100%;
  padding: 10px;
  border: 1px solid #dfdfdf;
}
.completed {
  font-style: italic;
  color: #595959;
  opacity: 0.4;
  text-decoration: line-through;
}

.todo-container {
  cursor: pointer;
}
</style>
