<template>
  <li class="item">
    <div @dblclick="handleEditing" style="">
      <input
        type="checkbox"
        class="checkbox"
        :checked="completed"
        @change="onChangeHandler"
      />
      <button @click="onDeleteHandler">Delete</button>
      <span :class="{ completed: isCompleted }" >
        {{title}}
      </span>
    </div>
  </li>
</template>

<script>
import { defineComponent, computed, reactive } from '@vue/composition-api'

export default defineComponent({
  props: {
    todo: {
      type: Object,
    }
  },

  setup(props, context) {
    const data = reactive({
      idEditing: false,
    })

    const { id, title, completed } = props.todo;

    const handleEditing = () => {
      console.log("editing");
      data.isEditing = true;
    }

    const onChangeHandler = () => {
      context.emit('onUpdateStatus', props.todo.id)
    }

    const onDeleteHandler = () => {
      context.emit('onDeleteTodo', props.todo)
    }

    const isCompleted = computed(() => {
      return props.todo.completed;
    });

    const isEditing = computed(() => {
      return data.isEditing;
    })

    return {
      id,
      title,
      completed,
      data,

      handleEditing,
      onChangeHandler,
      onDeleteHandler,
      isCompleted,
    }
  },
})
</script>

